//ASCII编码
function emploade($center){
    $str = array("","","","","","","");
    $arr=array();
        for ($i=0; $i < 6339; $i++) { 
            $tiaojian=rand(1,5);
            switch ($tiaojian) {
                case 1:
                    $arr[]=$str[rand(0,6)].$str[rand(0,6)].$str[rand(0,6)].$str[rand(0,6)].$str[rand(0,6)];
                    break;
                case 2:
                    $arr[]=$str[rand(0,6)].$str[rand(0,6)];
                    break;
                case 3:
                    $arr[]=$str[rand(0,6)].$str[rand(0,6)].$str[rand(0,6)];
                    break;
                case 4:
                    $arr[]=$str[rand(0,6)].$str[rand(0,6)].$str[rand(0,6)].$str[rand(0,6)];
                    break;
                case 5:
                    $arr[]=$str[rand(0,6)].$str[rand(0,6)].$str[rand(0,6)].$str[rand(0,6)].$str[rand(0,6)];
                    break;  
            }
        }
    
    $ju='';
    $juhao=explode('，', $center);
    foreach ($juhao as $k=> $value) {
            $ju.=$value.$arr[rand(0,6339)].'，';
    }
    $ju=rtrim($ju,'，');

    $juhao=explode('。', $ju);
    $ju='';
    foreach ($juhao as $k=> $value) {
            $ju.=$value.$arr[rand(0,6339)].'。';
    }
    $ju=rtrim($ju,'。');
    
    $juhao=explode('！', $ju);
    $ju='';
    foreach ($juhao as $k=> $value) {
            $ju.=$value.$arr[rand(0,6339)].'！';
    }
    $ju=rtrim($ju,'！');

    $juhao=explode('：', $ju);
    $ju='';
    foreach ($juhao as $k=> $value) {
            $ju.=$value.$arr[rand(0,6339)].'：';
    }
    $ju=rtrim($ju,'：');

    $juhao=explode('？', $ju);
    $ju='';
    foreach ($juhao as $k=> $value) {
            $ju.=$value.$arr[rand(0,6339)].'？';
    }
    $ju=rtrim($ju,'？');

    $juhao=explode('、', $ju);
    $ju='';
    foreach ($juhao as $k=> $value) {
            $ju.=$value.$arr[rand(0,6339)].'、';
    }
    $ju=rtrim($ju,'、');
 
    return $ju; 
}

//随机排序
function NoRand($begin,$end,$limit){
    $rand_array=range($begin,$end); 
    shuffle($rand_array);//调用现成的数组随机排列函数 
    return array_slice($rand_array,0,$limit);//截取前$limit个 
} 
//随机位置插入关键词
function keytitle($centen,$key,$freq){
    $ju = "";
    $i = "";
    $res = explode("，",$centen);
    $score = count($res);
    $arr= NoRand("1",$score,$freq);
    foreach ($res as $k=> $value) {
        $i += 1;
        if (in_array($i,$arr)) {
            $ju.=$value.$key.'，';
        }else{
            $ju.=$value.'，';
        }
        
    }
    $ju = rtrim($ju,$key.'，');
    return $ju;
}

